import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Leaderboard.css';

// Mock leaderboard data - in a real app, this would come from an API call
const mockLeaderboardData = {
  daily: [
    { id: 1, username: 'MazeKing', score: 950, fogPercentage: '92.5%', difficulty: 'hard' },
    { id: 2, username: 'FogMaster', score: 875, fogPercentage: '87.8%', difficulty: 'hard' },
    { id: 3, username: 'PathFinder', score: 810, fogPercentage: '86.1%', difficulty: 'medium' },
    { id: 4, username: 'SpeedRunner', score: 785, fogPercentage: '75.4%', difficulty: 'hard' },
    { id: 5, username: 'NightWalker', score: 720, fogPercentage: '80.2%', difficulty: 'medium' },
    { id: 6, username: 'MapExplorer', score: 695, fogPercentage: '83.7%', difficulty: 'easy' },
    { id: 7, username: 'MazeHacker', score: 650, fogPercentage: '72.8%', difficulty: 'medium' },
    { id: 8, username: 'FogHunter', score: 625, fogPercentage: '69.3%', difficulty: 'hard' },
    { id: 9, username: 'PlayerOne', score: 590, fogPercentage: '74.1%', difficulty: 'medium' },
    { id: 10, username: 'MazeLover', score: 560, fogPercentage: '70.5%', difficulty: 'easy' },
  ],
  weekly: [
    { id: 1, username: 'UltraMazer', score: 1250, fogPercentage: '95.8%', difficulty: 'hard' },
    { id: 2, username: 'MazeKing', score: 1150, fogPercentage: '93.2%', difficulty: 'hard' },
    { id: 3, username: 'FogMaster', score: 1080, fogPercentage: '91.7%', difficulty: 'hard' },
    { id: 4, username: 'LegendaryPath', score: 980, fogPercentage: '89.4%', difficulty: 'hard' },
    { id: 5, username: 'MazeGenius', score: 945, fogPercentage: '87.9%', difficulty: 'hard' },
    { id: 6, username: 'PathFinder', score: 890, fogPercentage: '86.3%', difficulty: 'medium' },
    { id: 7, username: 'SpeedRunner', score: 850, fogPercentage: '82.1%', difficulty: 'hard' },
    { id: 8, username: 'NightWalker', score: 810, fogPercentage: '84.7%', difficulty: 'medium' },
    { id: 9, username: 'MapExplorer', score: 780, fogPercentage: '85.9%', difficulty: 'easy' },
    { id: 10, username: 'MazeHunter', score: 765, fogPercentage: '80.3%', difficulty: 'medium' },
  ],
  allTime: [
    { id: 1, username: 'GrandMazeKeeper', score: 1500, fogPercentage: '97.2%', difficulty: 'hard' },
    { id: 2, username: 'UltraMazer', score: 1350, fogPercentage: '96.5%', difficulty: 'hard' },
    { id: 3, username: 'MazeKing', score: 1300, fogPercentage: '95.8%', difficulty: 'hard' },
    { id: 4, username: 'FogLegend', score: 1250, fogPercentage: '94.7%', difficulty: 'hard' },
    { id: 5, username: 'MazeGod', score: 1200, fogPercentage: '93.9%', difficulty: 'hard' },
    { id: 6, username: 'FogMaster', score: 1150, fogPercentage: '92.8%', difficulty: 'hard' },
    { id: 7, username: 'MazeWizard', score: 1100, fogPercentage: '91.5%', difficulty: 'hard' },
    { id: 8, username: 'LegendaryPath', score: 1050, fogPercentage: '90.4%', difficulty: 'hard' },
    { id: 9, username: 'MazeGenius', score: 1000, fogPercentage: '89.2%', difficulty: 'hard' },
    { id: 10, username: 'FogWarrior', score: 950, fogPercentage: '88.7%', difficulty: 'hard' },
  ]
};

// Difficulty badge colors
const difficultyColors = {
  easy: 'bg-easy',
  medium: 'bg-medium',
  hard: 'bg-hard'
};

const Leaderboard = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('daily');
  const [activeCategory, setActiveCategory] = useState('all');
  const [leaderboardData, setLeaderboardData] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch leaderboard data
  useEffect(() => {
    // In a real app, this would be an API call
    // api.getLeaderboard(activeTab, activeCategory).then(...)
    
    // Simulate API call with mock data
    setLoading(true);
    setTimeout(() => {
      setLeaderboardData(mockLeaderboardData[activeTab]);
      setLoading(false);
    }, 500);
  }, [activeTab, activeCategory]);

  // Filter data based on active category
  const filteredData = activeCategory === 'all' 
    ? leaderboardData 
    : leaderboardData.filter(entry => entry.difficulty === activeCategory);

  const handleBackToMenu = () => {
    navigate('/');
  };

  // Tabs for different time periods
  const renderTabs = () => (
    <div className="leaderboard-tabs">
      <button 
        className={`tab ${activeTab === 'daily' ? 'active' : ''}`} 
        onClick={() => setActiveTab('daily')}
      >
        Daily
      </button>
      <button 
        className={`tab ${activeTab === 'weekly' ? 'active' : ''}`} 
        onClick={() => setActiveTab('weekly')}
      >
        Weekly
      </button>
      <button 
        className={`tab ${activeTab === 'allTime' ? 'active' : ''}`} 
        onClick={() => setActiveTab('allTime')}
      >
        All Time
      </button>
    </div>
  );

  // Category filter
  const renderCategoryFilter = () => (
    <div className="category-filter">
      <button 
        className={`filter-btn ${activeCategory === 'all' ? 'active' : ''}`} 
        onClick={() => setActiveCategory('all')}
      >
        All
      </button>
      <button 
        className={`filter-btn ${activeCategory === 'easy' ? 'active' : ''}`} 
        onClick={() => setActiveCategory('easy')}
      >
        Easy
      </button>
      <button 
        className={`filter-btn ${activeCategory === 'medium' ? 'active' : ''}`} 
        onClick={() => setActiveCategory('medium')}
      >
        Medium
      </button>
      <button 
        className={`filter-btn ${activeCategory === 'hard' ? 'active' : ''}`} 
        onClick={() => setActiveCategory('hard')}
      >
        Hard
      </button>
    </div>
  );

  // Leaderboard table
  const renderLeaderboard = () => {
    if (loading) {
      return <div className="loading-spinner"></div>;
    }

    if (filteredData.length === 0) {
      return <div className="no-data">No scores found for this category</div>;
    }

    return (
      <table className="leaderboard-table">
        <thead>
          <tr>
            <th>Rank</th>
            <th>Player</th>
            <th>Score</th>
            <th>Fog Preserved</th>
            <th>Difficulty</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((entry, index) => (
            <tr key={entry.id} className={index < 3 ? 'top-rank' : ''}>
              <td className="rank-cell">
                {index < 3 ? (
                  <div className={`rank-badge rank-${index + 1}`}>{index + 1}</div>
                ) : (
                  <span>{index + 1}</span>
                )}
              </td>
              <td className="player-cell">{entry.username}</td>
              <td className="score-cell">{entry.score}</td>
              <td className="fog-cell">{entry.fogPercentage}</td>
              <td className="difficulty-cell">
                <span className={`difficulty-badge ${difficultyColors[entry.difficulty]}`}>
                  {entry.difficulty}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  // Your stats section
  const renderYourStats = () => {
    // In a real app, this would be based on the user's achievements
    const userRank = {
      daily: 42,
      weekly: 78,
      allTime: 156
    };

    const bestScore = {
      daily: 485,
      weekly: 485,
      allTime: 610
    };

    return (
      <div className="your-stats">
        <h3>Your Stats</h3>
        <div className="stats-grid">
          <div className="stat-item">
            <span className="stat-label">Your Rank</span>
            <span className="stat-value">{userRank[activeTab]}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Best Score</span>
            <span className="stat-value">{bestScore[activeTab]}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Scores Submitted</span>
            <span className="stat-value">12</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Average Score</span>
            <span className="stat-value">378</span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="leaderboard-container">
      <h1>Leaderboards</h1>
      
      {renderTabs()}
      {renderCategoryFilter()}
      
      <div className="leaderboard-content">
        {renderLeaderboard()}
      </div>
      
      {renderYourStats()}
      
      <div className="leaderboard-footer">
        <button className="back-button" onClick={handleBackToMenu}>
          Back to Menu
        </button>
      </div>
    </div>
  );
};

export default Leaderboard;
